\u0930 \\u0930\\u093f\\u0915\\u0949\\u0930\\u094d\\u0921: \\u0905\\u092a\\u0940\\u0932\\u0940\\u092f\\u0915\\u0930\\u094d\\u0924\\u093e \\u0915\\u094b \\u0905\\u092a\\u0940\\u0932 \\u092a\\u0930 \\u0930\\u093f\\u0915\\u0949\\u0930\\u094d\\u0921 \\u0924\\u0948\\u092f\\u093e\\u0930 \\u0915\\u0930\\u0928\\u0947 \\u0914\\u0930 \\u092a\\u094d\\u0930\\u0938\\u094d\\u0924\\u0941\\u0924 \\u0915\\u0930\\u0928\\u0947 \\u0915\\u0940 \\u0906\\u0935\\u0936\\u094d\\u092f\\u0915\\u0924\\u093e \\u0939\\u0948, \\u091c\\u093f\\u0938\\u092e\\u0947\\u0902 \\u0924\\u0925\\u094d\\u092f\\u094b\\u0902 \\u0915\\u093e \\u0935\\u093f\\u0935\\u0930\\u0923, \\u092a\\u094d\\u0930\\u092e\\u093e\\u0923, \\u092a\\u094d\\u0930\\u0926\\u0930\\u094d\\u0936\\u0928\\u0940 \\u0914\\u0930 \\u0928\\u093f\\u091a\\u0932\\u0947 \\u0928\\u094d\\u092f\\u093e\\u092f\\u093e\\u0932\\u092f \\u0915\\u093e \\u0932\\u093f\\u0916\\u093f\\u0924 \\u0928\\u093f\\u0930\\u094d\\u0923\\u092f \\u0936\\u093e\\u092e\\u093f\\u0932 \\u0939\\u094b\\u0924\\u093e \\u0939\\u0948\\u0964 \\u092f\\u0939 \\u0930\\u093f\\u0915\\u0949\\u0930\\u094d\\u0921 \\u0905\\u092a\\u0940\\u0932\\u0940\\u092f \\u0905\\u0926\\u093e\\u0932\\u0924 \\u0915\\u0940 \\u0938\\u092e\\u0940\\u0915\\u094d\\u0937\\u093e \\u0915\\u0947 \\u0932\\u093f\\u090f \\u0906\\u0927\\u093e\\u0930 \\u092c\\u0928\\u0924\\u093e \\u0939\\u0948\\u0964<\\/p>\\r\\n\\t<\\/li>\\r\\n\\t<li>\\r\\n\\t<p>\\u092c\\u094d\\u0930\\u0940\\u092b\\u094d\\u0938: \\u0905\\u092a\\u0940\\u0932\\u0940\\u092f\\u0915\\u0930\\u094d\\u0924\\u093e \\u0914\\u0930 \\u0935\\u093f\\u0930\\u094b\\u0927\\u0940 \\u092a\\u0915\\u094d\\u0937 (\\u0905\\u092a\\u0940\\u0932\\u0940) \\u0926\\u094b\\u0928\\u094b\\u0902 \\u0915\\u094b \\u0932\\u093f\\u0916\\u093f\\u0924 \\u092c\\u094d\\u0930\\u0940\\u092b\\u094d\\u0938 \\u092a\\u094d\\u0930\\u0938\\u094d\\u0924\\u0941\\u0924 \\u0915\\u0930\\u0928\\u0947 \\u0915\\u0940 \\u0906\\u0935\\u0936\\u094d\\u092f\\u0915\\u0924\\u093e \\u0939\\u0948\\u0902 \\u091c\\u094b \\u0909\